  ProCon = (function() {

    function ProCon() {}

    ProCon.prototype.data = {
      mutex: 1,
      empty: N,
      full: 0,
      front: 0,
      rear: 0,
      buf: []
    };

    ProCon.prototype.init = function() {
      var i, newDom,
        _this = this;
