      $("<p class="desPro">" + str + "</p>").insertBefore($("#des p:first"));
      return $($('.bufCover')[this.data.front]).animate({
        "height": "50px"
      });
    };

    ProCon.prototype.removeItem = function() {
      var str;
      this.data.rear = (this.data.rear + 1) % N;
      this.data.buf[this.data.rear] = 'e';
